var next = require('next');

var app = next({
  dev: false
});
var handle = app.getRequestHandler();

module.exports.handler = function (req, res) {
  return app.prepare().then(function () {
    return handle(req, res);
  })["catch"](function (ex) {
    console.error(ex.stack);
    process.exit(1);
  });
};
